import React, { useState, useEffect } from "react";
import { Button, Form, Select, Upload, Space } from "antd";
import { UploadOutlined } from "@ant-design/icons";
import Header from "../Common/Header/Header";
import NavHeader from "../Common/Header/NavHeader/NavHeader";
import styled from "styled-components";
import Layouts from "../Common/Layout";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const { Option } = Select;

const formItemLayout = {
  labelCol: {
    span: 6,
  },
  wrapperCol: {
    span: 14,
  },
};

const normFile = (e) => {
  console.log("Upload event:", e);
  if (Array.isArray(e)) {
    return e;
  }
  return e?.fileList;
};

const onFinish = (values) => {
  console.log("Received values of form: ", values);
};

export default function WritePost() {
  const [form] = Form.useForm();

  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [category, setCategory] = useState("");
  const [url, setUrl] = useState(false);

  const navigate = useNavigate();

  const handleTitleChange = (event) => {
    setTitle(event.currentTarget.value);
    console.log("title: ", event.currentTarget.value);
  };

  const handleContentChange = (event) => {
    setContent(event.currentTarget.value);
    console.log("content: ", event.currentTarget.value);
  };

  const handleCategoryChange = (value) => {
    setCategory(value);
    console.log("category: ", value);
  };

  const handleWritePost = async (postParam) => {
    try {
      const response = await axios.post(
        "http://localhost:8080/hello",
        postParam
        // {
        //   title,
        //   content,
        // }
      );

      // navigate("/");
      // window.location.replace("/");
      console.log(response);
    } catch (error) {
      console.log("실패하였습니다", error);
    }
  };

  // const handleImageUpload = async (file) => {
  //   try {
  //     const formData = new FormData();
  //     formData.append("multipartFile", file);

  //     const response = await axios.post(
  //       "http://localhost:8080/api/upload/image/post",
  //       formData
  //     );

  //     console.log("Image upload response:", response);
  //     return response.data.url;
  //   } catch (error) {
  //     console.error("Image upload failed:", error);
  //     throw error;
  //   }
  // };

  return (
    <>
      <Header />
      <NavHeader />
      <Layouts>
        <Container>
          <Body>
            <Form
              form={form}
              name="validateOnly"
              layout="vertical"
              autoComplete="off"
              {...formItemLayout}
              onFinish={onFinish}
              initialValues={{
                name: "",
                age: "",
                select: undefined,
                upload: null,
              }}
            >
              <Form.Item
                rules={[{ required: true, message: "제목을 입력해주세요." }]}
              >
                <Input
                  height="54px"
                  fontSize="30px"
                  placeholder="제목"
                  value={title}
                  onChange={handleTitleChange}
                />
              </Form.Item>
              <Form.Item
                rules={[{ required: true, message: "본문을 입력해주세요." }]}
              >
                <Input
                  height="800px"
                  fontSize="14px"
                  type="text"
                  placeholder="본문을 입력해주세요!"
                  style={{
                    height: "400px",
                  }}
                  value={content}
                  onChange={handleContentChange}
                />
              </Form.Item>
              <Form.Item
                label="Category"
                rules={[{ required: true, message: "이미지를 선택해주세요." }]}
                style={{
                  width: "400px",
                }}
              >
                <Select
                  placeholder="카테고리"
                  value={category}
                  onChange={handleCategoryChange}
                  rules={[
                    { required: true, message: "이미지를 선택해주세요." },
                  ]}
                >
                  <Option value="AI">인공지능</Option>
                  <Option value="BACKEND">백엔드</Option>
                  <Option value="DEVOPS">데브옵스</Option>
                  <Option value="EMBEDDED">임베디드</Option>
                  <Option value="FRONTEND">프론트엔드</Option>
                  <Option value="GAME">게임</Option>
                </Select>
              </Form.Item>

              <Form.Item
                name="upload"
                label="Upload"
                valuePropName="fileList"
                getValueFromEvent={normFile}
                rules={[{ required: true, message: "이미지를 선택해주세요." }]}
                style={{
                  textAlign: "left",
                }}
              >
                {/* <Upload
                  name="logo"
                  beforeUpload={(file) => {
                    handleImageUpload(file).then((imageUrl) => {
                      form.setFieldsValue({
                        upload: [
                          {
                            url: imageUrl,
                            uid: file.uid,
                            name: file.name,
                            status: "done",
                          },
                        ],
                      });
                    });

                    return false;
                  }}
                  // listType="picture"
                >
                  <Button icon={<UploadOutlined />}>Click to upload</Button>
                </Upload> */}
                <Upload
                  name="logo"
                  beforeUpload={(file) => {
                    // Get the current value of the upload field
                    const currentFiles = form.getFieldValue("upload") || [];

                    // Append the new file to the existing file list
                    const updatedFiles = [
                      ...currentFiles,
                      {
                        uid: file.uid,
                        name: file.name,
                        status: "done",
                        url: URL.createObjectURL(file), // Use this if you want to display the image preview
                      },
                    ];

                    // Set the updated file list back to the form
                    form.setFieldsValue({
                      upload: updatedFiles,
                    });

                    // Call the post API here (adjust as needed)
                    handleImageUpload({
                      title,
                      content,
                      category,
                      upload: updatedFiles,
                    });

                    // Prevent default behavior of Upload component
                    return false;
                  }}
                  listType="picture"
                >
                  <Button icon={<UploadOutlined />}>Click to upload</Button>
                </Upload>
              </Form.Item>

              <Form.Item wrapperCol={{ span: 12, offset: 6 }}>
                <Space>
                  <Button
                    type="primary"
                    htmlType="submit"
                    onClick={() => handleWritePost(title)}
                  >
                    Submit
                  </Button>
                  <Button htmlType="reset">reset</Button>
                </Space>
              </Form.Item>
            </Form>
          </Body>
        </Container>
      </Layouts>
    </>
  );
}

const Container = styled.div`
  min-width: 1050px;
  margin-top: 30px;
  margin-bottom: 60px;
  display: flex;
`;

const Body = styled.div`
  width: 100%;
  margin: 0px auto;
  letter-spacing: -0.6px;
  text-align: center;
`;

const Input = styled.textarea`
  height: ${(props) => props.height || "44px"};
  width: ${(props) => props.width || "1000px"};
  padding: 0px 11px 1px 15px;
  border: none;
  border-bottom: 1px solid rgb(221, 221, 221);
  font-weight: 400;
  font-size: ${(props) => props.fontSize || "16px"};
  line-height: 1.6;
  color: rgb(51, 51, 51);
  outline: none;
  resize: none;
`;
