import React, { useState, useEffect } from "react";
import { Button, Form, Select, Upload, Space } from "antd";
import { UploadOutlined } from "@ant-design/icons";
import Header from "../Common/Header/Header";
import NavHeader from "../Common/Header/NavHeader/NavHeader";
import styled from "styled-components";
import Layouts from "../Common/Layout";
import { useRef } from "react";
import { useCallback } from "react";

const { Option } = Select;

const formItemLayout = {
  labelCol: {
    span: 6,
  },
  wrapperCol: {
    span: 14,
  },
};

const normFile = (e) => {
  console.log("Upload event:", e);
  if (Array.isArray(e)) {
    return e;
  }
  return e?.fileList;
};

const onFinish = (values) => {
  console.log("Received values of form: ", values);
};

export default function WritePost() {
  const [form] = Form.useForm();

  const textRef = useRef();
  const handleResizeHeight = useCallback(() => {
    textRef.current.style.height = textRef.current.scrollHeight + "px";
  }, []);

  const textAreaRef = useRef(null);
  const [textAreaHeight, setTextAreaHeight] = useState("auto");
  const [parentHeight, setParentHeight] = useState("auto");

  const parentStyle = {
    minHeight: parentHeight,
  };

  const textAreaStyle = {
    height: textAreaHeight,
  };

  // 댓글의 내용이 변할 때마다 textAreaRef의 높이를 조정해 준다.
  useEffect(() => {
    if (textAreaRef && textAreaRef.current) {
      setParentHeight(`${textAreaRef.current.scrollHeight}px`);
      setTextAreaHeight(`${textAreaRef.current.scrollHeight}px`);
    }
  }, [message]);

  // 댓글의 값이 변할 때 추가로 높이를 보정해 준다.
  const onChangeHandler = (event) => {
    setTextAreaHeight("auto");
    setParentHeight(`${textAreaRef.current.scrollHeight}px`);
    setMessage(event.target.value);
  };

  return (
    <>
      <Header />
      <NavHeader />
      <Layouts>
        <Container>
          <Body>
            <Form
              form={form}
              name="validateOnly"
              layout="vertical"
              autoComplete="off"
              {...formItemLayout}
              onFinish={onFinish}
              initialValues={{
                name: "",
                age: "",
                select: undefined,
                upload: null,
              }}
            >
              <Form.Item
                rules={[{ required: true, message: "제목을 입력해주세요." }]}
              >
                <Input
                  height="54px"
                  fontSize="30px"
                  placeholder="제목"
                  style={{}}
                />
              </Form.Item>
              <Form.Item
                rules={[{ required: true, message: "본문을 입력해주세요." }]}
              >
                <Input
                  fontSize="14px"
                  type="text"
                  placeholder="본문을 입력해주세요!"
                //   onInput={handleResizeHeight}
                //   style={{
                //     height: "400px",
                //   }}
                onChange={(e) => onChangeHandler(e)}
      ref={textAreaRef}
      style={{
        height: textAreaHeight,
        overflow: "hidden",
      }}
      rows={3}
                />
              </Form.Item>
              <Form.Item
                style={{
                  width: "400px",
                }}
              >
                <Select placeholder="인공지능">
                  <Option value="AI">인공지능</Option>
                  <Option value="BACKEND">백엔드</Option>
                  <Option value="DEVOPS">데브옵스</Option>
                  <Option value="EMBEDDED">임베디드</Option>
                  <Option value="FRONTEND">프론트엔드</Option>
                  <Option value="GAME">게임</Option>
                </Select>
              </Form.Item>

              <Form.Item
                name="upload"
                label="Upload"
                valuePropName="fileList"
                getValueFromEvent={normFile}
                rules={[{ required: true, message: "이미지를 선택해주세요." }]}
                style={{
                  textAlign: "left",
                }}
              >
                <Upload name="logo" action="/upload.do" listType="picture">
                  <Button icon={<UploadOutlined />}>Click to upload</Button>
                </Upload>
              </Form.Item>

              <Form.Item wrapperCol={{ span: 12, offset: 6 }}>
                <Space>
                  <Button type="primary" htmlType="submit">
                    Submit
                  </Button>
                  <Button htmlType="reset">reset</Button>
                </Space>
              </Form.Item>
            </Form>
          </Body>
        </Container>
      </Layouts>
    </>
  );
}

const MainWrap = styled.div`
  width: 1050px;
  margin: 0px auto;
  padding: 60px 0px;
  text-align: center;
  justify-content: center;
  align-items: center;
`;

const Container = styled.div`
  min-width: 1050px;
  margin-top: 30px;
  margin-bottom: 60px;
  display: flex;
`;

const Body = styled.div`
  width: 100%;
  margin: 0px auto;
  letter-spacing: -0.6px;
  text-align: center;
`;

const Input = styled.textarea`
  height: ${(props) => props.height || "44px"};
  width: ${(props) => props.width || "1000px"};
  padding: 0px 11px 1px 15px;
  border: none;
  border-bottom: 1px solid rgb(221, 221, 221);
  font-weight: 400;
  font-size: ${(props) => props.fontSize || "16px"};
  line-height: 1.6;
  color: rgb(51, 51, 51);
  outline: none;
  resize: none;
`;
