import React from "react";
import { Button, Form, Select, Space, Upload, Input } from "antd";
import { UploadOutlined } from "@ant-design/icons";
import Header from "../Common/Header/Header";
import NavHeader from "../Common/Header/NavHeader/NavHeader";
import styled from "styled-components";
import { useState } from "react";
import { useEffect } from "react";

const { Option } = Select;

const formItemLayout = {
  labelCol: {
    span: 6,
  },
  wrapperCol: {
    span: 14,
  },
};

const normFile = (e) => {
  console.log("Upload event:", e);
  if (Array.isArray(e)) {
    return e;
  }
  return e?.fileList;
};

const onFinish = (values) => {
  console.log("Received values of form: ", values);
};

const SubmitButton = ({ form }) => {
  const [submittable, setSubmittable] = useState(false);

  // Watch all values
  const values = form.getFieldsValue();

  useEffect(() => {
    form.validateFields(["name", "age"], { validateOnly: true }).then(
      () => {
        setSubmittable(true);
      },
      () => {
        setSubmittable(false);
      }
    );
  }, [values]);

  return (
    <Button type="primary" htmlType="submit" disabled={!submittable}>
      Submit
    </Button>
  );
};

export default function WritePost() {
  const [form] = Form.useForm();

  return (
    <>
      <Header />
      <NavHeader />
      <MainWrap>
        <Form
          form={form}
          name="validateOnly"
          layout="vertical"
          autoComplete="off"
        >
          <Form.Item name="name" label="Name" rules={[{ required: true }]}>
            <Input />
          </Form.Item>
          <Form.Item name="age" label="Age" rules={[{ required: true }]}>
            <Input />
          </Form.Item>
          <Form.Item
            name="select"
            label="Select"
            hasFeedback
            rules={[{ required: true, message: "Please select your country!" }]}
          >
            <Select placeholder="Please select a country">
              <Option value="AI">인공지능</Option>
              <Option value="BACKEND">백엔드</Option>
              <Option value="DEVOPS">데브옵스</Option>
              <Option value="EMBEDDED">임베디드</Option>
              <Option value="FRONTEND">프론트엔드</Option>
              <Option value="GAME">게임</Option>
            </Select>
          </Form.Item>
          <Form.Item>
            <Space>
              <SubmitButton form={form} />
              <Button htmlType="reset">Reset</Button>
            </Space>
          </Form.Item>
        </Form>
      </MainWrap>
    </>
  );
}

export const MainWrap = styled.div`
  width: 1050px;
  margin: 0px auto;
  padding: 60px 0px;
`;
