import React from "react";
import styled from "styled-components";
import { useEffect, useState } from "react";

export default function Deals() {
  const [hour, setHour] = useState(23 - new Date().getHours());
  const [minute, setMinute] = useState(59 - new Date().getMinutes());
  const [second, SetSecond] = useState(59 - new Date().getSeconds());

  useEffect(() => {
    const id = setInterval(() => {
      setHour(23 - new Date().getHours());
      setMinute(59 - new Date().getMinutes());
      SetSecond(59 - new Date().getSeconds());
    }, 1000);
    return () => clearInterval(id);
  }, []);

  return (
    <>
      <DealWrap>
        <DealBox>
          <TimeBox>
            <h2>일일특가</h2>
            <h4>24시간 한정 특가</h4>
            <Timer>
              <TimerIcon>
                <div class="lottie-timer">
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    viewBox="0 0 36 36"
                    width="36"
                    height="36"
                    preserveAspectRatio="xMidYMid meet"
                    style="width: 100%; height: 100%; transform: translate3d(0px, 0px, 0px);"
                  >
                    <defs>
                      <clipPath id="__lottie_element_12">
                        <rect width="36" height="36" x="0" y="0"></rect>
                      </clipPath>
                    </defs>
                    <g clip-path="url(#__lottie_element_12)">
                      <g
                        transform="matrix(1,0,0,1,3.75,3.75)"
                        opacity="1"
                        style="display: block;"
                      >
                        <g opacity="1" transform="matrix(1,0,0,1,14.25,14.25)">
                          <path
                            fill="rgb(189,118,255)"
                            fill-opacity="1"
                            d=" M14,0 C14,7.73199987411499 7.73199987411499,14 0,14 C-7.73199987411499,14 -14,7.73199987411499 -14,0 C-14,-7.73199987411499 -7.73199987411499,-14 0,-14 C7.73199987411499,-14 14,-7.73199987411499 14,0z"
                          ></path>
                        </g>
                        <g opacity="1" transform="matrix(1,0,0,1,0,0)">
                          <path
