import React, { useState } from "react";
import axios from "axios"; // You may need to install axios if not already done: npm install axios

const AddProduct = () => {
  const [product, setProduct] = useState({
    category: "",
    brand: "",
    name: "",
    description: "",
    price: "",
    file: null,
    stockQuantity: "",
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setProduct((prevProduct) => ({ ...prevProduct, [name]: value }));
  };

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    setProduct((prevProduct) => ({ ...prevProduct, file }));
  };

  const uploadProduct = async (e) => {
    e.preventDefault();

    // You may want to perform additional validation before sending the data
    // ...

    // Use axios or fetch to send the product data to the server
    try {
      const response = await axios.post("/api/addProduct", product);
      console.log("Product uploaded successfully:", response.data);
    } catch (error) {
      console.error("Error uploading product:", error);
    }
  };

  return (
    <div className="container">
      <form
        id="product-form"
        name="newProduct"
        className="form-horizontal"
        encType="multipart/form-data"
      >
        {/* Render form fields similar to the JSP version */}
        {/* ... */}

        <div className="form-group row">
          <label className="col-sm-2">상품 이미지</label>
          <div>
            <input
              type="file"
              name="file"
              className="form-control"
              onChange={handleFileChange}
            />
          </div>
        </div>

        <div className="form-group row">
          <label className="col-sm-2">상품 재고</label>
          <div>
            <input
              type="text"
              name="stockQuantity"
              className="form-control"
              placeholder="숫자만 입력하세요."
              onChange={handleInputChange}
            />
          </div>
        </div>

        <button className="cart__btn right" onClick={uploadProduct}>
          등록하기
        </button>
      </form>
    </div>
  );
};

export default AddProduct;
