import React, { useState, useEffect, useRef } from "react";
import { Button, Form, Select, Upload, Space, Tag, Tooltip } from "antd";
import { UploadOutlined } from "@ant-design/icons";
import Header from "../Common/Header/Header";
import NavHeader from "../Common/Header/NavHeader/NavHeader";
import styled from "styled-components";
import Layouts from "../Common/Layout";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { PlusOutlined } from "@ant-design/icons";

const { Option } = Select;

const formItemLayout = {
  labelCol: {
    span: 6,
  },
  wrapperCol: {
    span: 14,
  },
};

const normFile = (e) => {
  console.log("Upload event:", e);
  if (Array.isArray(e)) {
    return e;
  }
  return e?.fileList;
};

const onFinish = (values) => {
  console.log("Received values of form: ", values);
};

export default function WritePost() {
  const [form] = Form.useForm();

  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [category, setCategory] = useState("");
  const [url, setUrl] = useState(false);
  const [uploadedImages, setUploadedImages] = useState([]);
  const [tags, setTags] = useState(["Unremovable", "Tag 2", "Tag 3"]);
  const [inputVisible, setInputVisible] = useState(false);
  const [inputValue, setInputValue] = useState("");
  const [editInputIndex, setEditInputIndex] = useState(-1);
  const [editInputValue, setEditInputValue] = useState("");
  const inputRef = useRef < import("antd").InputRef > null;
  const editInputRef = useRef < import("antd").InputRef > null;

  const navigate = useNavigate();

  const handleTitleChange = (event) => {
    setTitle(event.currentTarget.value);
    console.log("title: ", event.currentTarget.value);
  };

  const handleContentChange = (event) => {
    setContent(event.currentTarget.value);
    console.log("content: ", event.currentTarget.value);
  };

  const handleCategoryChange = (value) => {
    setCategory(value);
    console.log("category: ", value);
  };

  const handleWritePost = async (postParam) => {
    try {
      const response = await axios.post(
        "http://localhost:8080/hello",
        postParam
        // {
        //   title,
        //   content,
        // }
      );

      // navigate("/");
      // window.location.replace("/");
      console.log(response);
    } catch (error) {
      console.log("실패하였습니다", error);
    }
  };

  const handleImageUpload = async (file) => {
    try {
      const formData = new FormData();
      formData.append("multipartFile", file);

      const response = await axios.post(
        "http://localhost:8080/api/upload/image/post",
        formData
      );

      console.log("Image upload response:", response);

      // Update the uploadedImages state with the new image
      setUploadedImages((prevImages) => [
        ...prevImages,
        {
          url: response.data.url,
          uid: file.uid,
          name: file.name,
          status: "done",
        },
      ]);

      return response.data.url;
    } catch (error) {
      console.error("Image upload failed:", error);
      throw error;
    }
  };

  useEffect(() => {
    if (inputVisible) {
      inputRef.current?.focus();
    }
  }, [inputVisible]);

  useEffect(() => {
    editInputRef.current?.focus();
  }, [editInputValue]);

  const handleClose = (removedTag: string) => {
    const newTags = tags.filter((tag) => tag !== removedTag);
    setTags(newTags);
  };

  const showInput = () => {
    setInputVisible(true);
  };

  const handleInputChange = (event) => {
    setInputValue(event.target.value);
  };

  const handleInputConfirm = () => {
    if (inputValue && !tags.includes(inputValue)) {
      setTags([...tags, inputValue]);
    }
    setInputVisible(false);
    setInputValue("");
  };

  const handleEditInputChange = (event) => {
    setEditInputValue(event.target.value);
  };

  const handleEditInputConfirm = () => {
    const newTags = [...tags];
    newTags[editInputIndex] = editInputValue;
    setTags(newTags);
    setEditInputIndex(-1);
    setEditInputValue("");
  };

  return (
    <>
      <Header />
      <NavHeader />
      <Layouts>
        <Container>
          <Body>
            <Form
              form={form}
              name="validateOnly"
              layout="vertical"
              autoComplete="off"
              {...formItemLayout}
              onFinish={onFinish}
              initialValues={{
                name: "",
                age: "",
                select: undefined,
                upload: null,
              }}
            >
              <Form.Item
                rules={[{ required: true, message: "제목을 입력해주세요." }]}
              >
                <Input
                  height="54px"
                  fontSize="30px"
                  placeholder="제목"
                  value={title}
                  onChange={handleTitleChange}
                />
              </Form.Item>
              <Form.Item
                rules={[{ required: true, message: "본문을 입력해주세요." }]}
              >
                <Input
                  height="800px"
                  fontSize="14px"
                  type="text"
                  placeholder="본문을 입력해주세요!"
                  style={{
                    height: "400px",
                  }}
                  value={content}
                  onChange={handleContentChange}
                />
              </Form.Item>
              <Form.Item
                label="Category"
                rules={[
                  { required: true, message: "카테고리를 선택해주세요." },
                ]}
                style={{
                  width: "400px",
                }}
              >

                <Space size={[0, 8]} wrap>
      {tags.map((tag, index) => {
        if (editInputIndex === index) {
          return (
            <Input
              ref={editInputRef}
              key={tag}
              size="small"
              style={tagInputStyle}
              value={editInputValue}
              onChange={handleEditInputChange}
              onBlur={handleEditInputConfirm}
              onPressEnter={handleEditInputConfirm}
            />
          );
        }
        const isLongTag = tag.length > 20;
        const tagElem = (
          <Tag
            key={tag}
            closable={index !== 0}
            style={{ userSelect: 'none' }}
            onClose={() => handleClose(tag)}
          >
            <span
              onDoubleClick={(e) => {
                if (index !== 0) {
                  setEditInputIndex(index);
                  setEditInputValue(tag);
                  e.preventDefault();
                }
              }}
            >
              {isLongTag ? `${tag.slice(0, 20)}...` : tag}
            </span>
          </Tag>
        );
        return isLongTag ? (
          <Tooltip title={tag} key={tag}>
            {tagElem}
          </Tooltip>
        ) : (
          tagElem
        );
      })}
      {inputVisible ? (
        <Input
          ref={inputRef}
          type="text"
          size="small"
          style={tagInputStyle}
          value={inputValue}
          onChange={handleInputChange}
          onBlur={handleInputConfirm}
          onPressEnter={handleInputConfirm}
        />
      ) : (
        <Tag style={tagPlusStyle} icon={<PlusOutlined />} onClick={showInput}>
          New Tag
        </Tag>
      )}
    </Space>
  );
};
                <Select
                  placeholder="카테고리"
                  value={category}
                  onChange={handleCategoryChange}
                  rules={[
                    { required: true, message: "이미지를 선택해주세요." },
                  ]}
                >
                  <Option value="AI">인공지능</Option>
                  <Option value="BACKEND">백엔드</Option>
                  <Option value="DEVOPS">데브옵스</Option>
                  <Option value="EMBEDDED">임베디드</Option>
                  <Option value="FRONTEND">프론트엔드</Option>
                  <Option value="GAME">게임</Option>
                </Select>
              </Form.Item>

              <Form.Item
                name="upload"
                label="Upload"
                valuePropName="fileList"
                getValueFromEvent={normFile}
                rules={[{ required: true, message: "이미지를 선택해주세요." }]}
                style={{
                  textAlign: "left",
                }}
              >
                <Upload
                  name="logo"
                  beforeUpload={(file) => {
                    handleImageUpload(file);
                    return false;
                  }}
                  listType="picture"
                >
                  <Button icon={<UploadOutlined />}>Click to upload</Button>
                </Upload>
              </Form.Item>

              <Form.Item wrapperCol={{ span: 12, offset: 6 }}>
                <Space>
                  <Button
                    type="primary"
                    htmlType="submit"
                    onClick={() => handleWritePost(title)}
                  >
                    Submit
                  </Button>
                  <Button htmlType="reset">reset</Button>
                </Space>
              </Form.Item>
            </Form>
          </Body>
        </Container>
      </Layouts>
    </>
  );
