import React from "react";
import styled from "styled-components";

export default function NewItemList(props) {
  return (
    <>
      <ItemWrapper to={``}>
        <ItemBox>
          <NewImageBox>
            <img src={props.url} alt="제안사진" />
            <div>
              <CartButton>
                <img
                  src="data:image/svg+xml;base64,"
                  alt="장바구니 아이콘"
                />
              </CartButton>
            </div>
          </NewImageBox>

          <ProductInfo>
            <h3>샛별배송</h3>
            <h4>{props.text}</h4>

            <PriceInfo>
              <PriceBox>
                <PirceDC>5%</PirceDC>
                <span>
                  {parseInt(props.price * 0.95).toLocaleString("ko-kr")}원
                </span>
              </PriceBox>
            </PriceInfo>
            <RealPrice>{props.price.toLocaleString("ko-kr")}원</RealPrice>
          </ProductInfo>
        </ItemBox>
      </ItemWrapper>
    </>
  );
}

export const ItemWrapper = styled.div`
  margin-left: 6px;
  flex-shrink: 0;
  width: 264.75px;
  height: 100%;
  position: relative;
  transition-property: transform;
  text-decoration: none;
  color: inherit;
  margin-top: 0;
`;
export const ItemBox = styled.div`
  color: rgb(51, 51, 51);
  cursor: pointer;
  width: 249px;
`;
export const NewImageBox = styled.div`
  overflow: hidden;
  position: relative;
  background-color: rgb(245, 245, 245);
  height: 320px;
  & img {
    width: 100%;
    height: 100%;
    object-fit: cover;
    transition: all 0.5s ease-in-out 0s;
  }
`;
export const CartButton = styled.div`
  position: absolute;
  right: 16px;
  bottom: 16px;
  z-index: 10;
  border: none;
  margin: 0;
  background-color: rgba(0, 0, 0, 0);
  & img {
    width: 45px;
    height: 45px;
  }
`;
export const ProductInfo = styled.div`
  position: relative;
  padding: 14px 10px 0px 0px;

  & h3 {
    display: block;
    padding-bottom: 2px;
    color: rgb(51, 51, 51);
    cursor: pointer;
}
  }

  & h4 {
    font-size: 16px;
    line-height: 1.45;
    font-weight: 400;
    margin-bottom: 8px;
    overflow: hidden;
    text-overflow: ellipsis;
    display: -webkit-box;
    -webkit-line-clamp: 2;
    -webkit-box-orient: vertical;
    letter-spacing: normal;
    word-break: break-word;
    overflow-wrap: break-word;
  }
`;
export const PriceInfo = styled.div`
  display: flex;
  -webkit-box-pack: justify;
  justify-content: space-between;
  -webkit-box-align: cen;
`;
export const PriceBox = styled.div`
  display: flex;
  flex-direction: row;

  & span {
    font-size: 16px;
    font-weight: 800;
    line-height: 1.5;
    white-space: nowrap;
  }
`;

export const PirceDC = styled.div`
  color: rgb(250, 98, 47);
  font-size: 16px;
  font-weight: 800;
  line-height: 1.5;
  white-space: nowrap;
  margin-right: 7px;
`;
export const RealPrice = styled.div`
  color: rgb(181, 181, 181);
  font-size: 14px;
  font-weight: 400;
  line-height: normal;
  text-decoration: line-through;
  margin-top: 2px;
`;
