import React, { useState } from "react";
import styled from "styled-components";

const PostForm = () => {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [image, setImage] = useState(null);

  const handleTitleChange = (e) => {
    setTitle(e.target.value);
  };

  const handleContentChange = (e) => {
    setContent(e.target.value);
  };

  const handleImageChange = (e) => {
    // Handle image upload logic here
    // You may want to use FileReader to convert the image to base64 or send it to a server
    // For simplicity, we are just storing the file object in state
    setImage(e.target.files[0]);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Handle the submission logic here
    console.log("Title:", title);
    console.log("Content:", content);
    console.log("Image:", image);
    // Add logic to send data to a server or perform other actions
  };

  return (
    <FormContainer>
      <form onSubmit={handleSubmit}>
        <label>
          Title:
          <input type="text" value={title} onChange={handleTitleChange} />
        </label>

        <label>
          Content:
          <textarea value={content} onChange={handleContentChange} />
        </label>

        <label>
          Image:
          <input type="file" accept="image/*" onChange={handleImageChange} />
        </label>

        <button type="submit">Submit</button>
      </form>
    </FormContainer>
  );
};

const FormContainer = styled.div`
  max-width: 400px;
  margin: 0 auto;
  padding: 20px;
  border: 1px solid #ccc;
`;

export default PostForm;
